
public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=(int)(Math.random()*50);
		System.out.println(num);
		
		if(num>20)
		{
			System.out.println("Number is greater than 20");
			
		}
		else
		{
			System.out.println("Number is less than 10");
		}
		
		
	}

}
